content.query:
let UserPrincipalName = '{UserPrincipalName}';
let MonitoredOperations = '{Operation}';
let ReportPeriod = ({Period});
let UnmonitoredUserIds = dynamic(["SHAREPOINT\\system"]);
let MonitoredData = OfficeActivity
| where TimeGenerated > ago(ReportPeriod)
| where Operation == MonitoredOperations and UserId !in (UnmonitoredUserIds) and UserId == UserPrincipalName
| where UserType == "Regular";
MonitoredData
| project TimeGenerated, ['SharePoint Site'] = Site_Url, ['File Name'] = SourceFileName, RecordType, ClientIP, EventSource
